import React from 'react';
import axios from 'axios';

class MovieVisualisation extends React.Component {
   constructor(props) {
     super(props)
     this.state = {
      movie_found: false,
      movie_id: '',
      movie_name : this.props.movieName,
      movie_overview : '',
      movie_release_date : '',
      positive_comments: '',
      negative_comments: '',
      neutral_comments: '',
      see_or_skip: '',
      date_analysed: ''
     }
   }

    getMovieInformation(movieName) {
      axios.get("https://api.themoviedb.org/3/search/movie", {
       params: {
         api_key : '146fa0756d99220f8811aceb8a865301',
         language : 'en-US',
         query : movie_name,
         page : '1',
         include_adult : 'false' 
       }
     }).then(apiResponse => this.setState({
       movie_id : apiResponse.data.results[0].id,
       movie_name : apiResponse.data.results[0].title,
       movie_overview : apiResponse.data.results[0].overview,
       movie_releaseDate : apiResponse.data.results[0].release_date
     }))
     .catch(error => {
       console.log(error)
     })
    }

    checkIfMovieAnalysed(movie_id) {
      axios.get("http://localhost:5000/movieSentiment/findMovieSentiments/" + movie_id)
      .then(apiResponse => this.setState({
        movie_found : true,
        positive_comments: apiResponse.data[0].positive_comments,
        negative_comments: apiResponse.data[0].negative_comments,
        neutral_comments: apiResponse.data[0].neutral_comments,
        see_or_skip: apiResponse.data[0].see_or_skip,
        date_analysed: apiResponse.data[0].date_analysed
      }))
      .catch(error => this.setState({
        movie_found : false
      }))
    }

    componentDidMount() {
      this.getMovieInformation(this.state.movieName)
      //this.checkIfMovieAnalysed(this.setState.movie_id)
    }

   render() {
     return (
       <div>
        <h1>This is what people think about:</h1>
        <h3>{this.state.movieName}</h3>
        <h3>{this.state.movieOverview}</h3>
        <h3>Release Date: {this.state.movieReleaseDate}</h3>
        <h3>{this.state.positive_comments}</h3>
        <h3>{this.state.negative_comments}</h3>
        <h3>{this.state.neutral_comments}</h3>
        <h3>{this.state.see_or_skip}</h3>
       </div>
     )
   }
}

export default MovieVisualisation;
