import React from 'react';
import axios from 'axios'
import {Autosuggest} from 'react-autosuggest'

class CompareMovieSelector extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            movie_entered : "",
            movie_suggestions : [],
            all_movies : []
        }
    }

    getAllMovies() {
        axios.get("http://localhost:5000/movieSentiment/findMovieSentiment")
        .then(apiResponse => {
            this.setState({
                all_movies : apiResponse.data
            })
            console.log(this.state.all_movies)
        })
        .catch(error => {
            console.log(error)
        })
    }
        
    getMovieSuggestions(movie_name) {
        const cleanMovieName = movie_name.trim().replace(/[.*+?^${}()|[\]\\]/g, '\\$&')
        if (cleanMovieName === '') {
            return [];
        }

        const regex = new RegExp('^' + cleanMovieName, 'i')
        return this.state.all_movies.filter(suggestion => regex.test(suggestion.movie_name))
    }
    
    getMovieSuggestionTitle(suggestion) {
        return suggestion.movie_name
    }
    
    renderSuggestion(suggestion) {
        return (
            <span>{suggestion.movie_name}</span>
        )
    }

    onChange = (event, { movie_name, method }) => {
        this.setState({
          movie_entered : movie_name
        })
    }
      
    onSuggestionsFetchRequested = ({ movie_name }) => {
        this.setState({
            movie_suggestions: this.getMovieSuggestions(movie_name)
        })
    }

    onSuggestionsClearRequested = () => {
        this.setState({
            movie_suggestions: []
        })
    }

    componentDidMount() {
        this.getAllMovies()
    }

    render() {
        const { movie_name, movie_suggestions } = this.state
        const inputProps = {
            placeholder: "Enter a movie",
            movie_name,
            onChange: this.onChange
        }

        return (
            <Autosuggest 
                suggestions={movie_suggestions}
                onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
                onSuggestionsClearRequested={this.onSuggestionsClearRequested}
                getSuggestionValue={this.getMovieSuggestionTitle}
                renderSuggestion={this.renderSuggestion}
                inputProps={inputProps} 
            />
        )
    }
}

export default CompareMovieSelector;
