import React from 'react'
import axios from 'axios'

class CompareMovieResults extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstMovieToCompare : {
                movieId : "",
                movieName : this.props.firstMovieToCompare,
                movieOverview : "",
                movieReleaseDate : "",
                posterPath : '',
                positiveComments: '',
                negativeComments: '',
                neutralComments: '',
                seeOrSkip: '',
            },

            secondMovieToCompare : {
                movieId : "",
                movieName : this.props.secondMovieToCompare,
                movieOverview : "",
                movieReleaseDate : "",
                posterPath : '',
                positiveComments: '',
                negativeComments: '',
                neutralComments: '',
                seeOrSkip: '',
            }
        }
    }

    getMovieInformation(movie) {
        axios.get("https://api.themoviedb.org/3/search/movie", {
            params: {
                api_key : '146fa0756d99220f8811aceb8a865301',
                language : 'en-US',
                query : movie.movieName,
                page : '1',
                include_adult : 'false' 
            }
        })
        .then(apiResponse => {
            const movieDetails = {
                movieId : apiResponse.data.results[0].id,
                movieName : apiResponse.data.results[0].title,
                movieOverview : apiResponse.data.results[0].overview,
                movieReleaseDate : apiResponse.data.results[0].release_date,
                posterPath : apiResponse.data.results[0].poster_path
            }

            return movieDetails
        })
        .catch(error => {
            console.log(error)
        })
    }

    getMovieSentiment(movieId) {
        axios.get("http://localhost:5000/movieSentiment/findMovieSentiment/" + movieId)
        .then(apiResponse => {
            if (apiResponse.data !== null) {
                const movieSentiment = {
                    positiveComments: apiResponse.data.positive_comments,
                    negativeComments: apiResponse.data.negative_comments,
                    neutralComments: apiResponse.data.neutral_comments,
                    seeOrSkip: apiResponse.data.see_or_skip,
                    dateAnalysed: apiResponse.data.date_analysed
                }
                return movieSentiment
            }
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        const firstMovieDetails = this.getMovieInformation(this.state.firstMovieToCompare)
        const firstMovieSentiment = this.getMovieSentiment(firstMovieDetails.movieId)
        const secondMovieDetails = this.getMovieInformation(this.state.secondMovieToCompare)
        const secondMovieSentiment = this.getMovieSentiment(secondMovieDetails.movieId)

        this.setState({
            firstMovieToCompare : {
                movieId : firstMovieDetails.movieId,
                movieOverview : firstMovieDetails.movieOverview,
                movieReleaseDate : firstMovieDetails.movieReleaseDate,
                posterPath : firstMovieDetails.posterPath,
                positiveComments: firstMovieSentiment.positiveComments,
                negativeComments: firstMovieSentiment.negativeComments,
                neutralComments: firstMovieSentiment.neutralComments,
                seeOrSkip: firstMovieSentiment.seeOrSkip,
            },

            secondMovieToCompare : {
                movieId : secondMovieDetails.movieId,
                movieOverview : secondMovieDetails.movieOverview,
                movieReleaseDate : secondMovieDetails.movieReleaseDate,
                posterPath : secondMovieDetails.posterPath,
                positiveComments: secondMovieSentiment.positiveComments,
                negativeComments: secondMovieSentiment.negativeComments,
                neutralComments: secondMovieSentiment.neutralComments,
                seeOrSkip: secondMovieSentiment.seeOrSkip,
            }
        })
    }

    render() {
        return (
            <div>
                <h1>Movie Results</h1>
            </div>
        )
    }
}

export default CompareMovieResults;
