import React from 'react';

class MovieVisualisation extends React.Component {
   constructor(props) {
     super(props)
     this.state = {
      error : null,
      movieName : this.props.movieName,
      apiResultsObtained: false,
      items: []
     }
   }

   async getMovieInformation(movieName) {
     const apiResponse = await axios.get("https://api.themoviedb.org/3/search/movie", {
       params: {
         api_key : '146fa0756d99220f8811aceb8a865301',
         language : 'en-US',
         query : movieName,
         page : '1',
         include_adult : 'false' 
       }
     })

     console.log(apiResponse)
     return apiResponse
   }

   render() {
     return (
       <div>
        <h1>This is what people think about:</h1>
        <h1>{this.props.movieName}</h1>
        <h1>{this.getMovieInformation(this.state.movieName)}</h1>
       </div>
     )
   }
}

export default MovieVisualisation;
