import React from 'react'
import axios from 'axios'

class CompareMovieResults extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            firstMovieToCompare : {
                movieId : "",
                movieName : this.props.firstMovieToCompare,
                movieOverview : "",
                movieReleaseDate : "",
                posterPath : '',
                positiveComments: '',
                negativeComments: '',
                neutralComments: '',
                seeOrSkip: '',
            },

            secondMovieToCompare : {
                movieId : "",
                movieName : this.props.secondMovieToCompare,
                movieOverview : "",
                movieReleaseDate : "",
                posterPath : '',
                positiveComments: '',
                negativeComments: '',
                neutralComments: '',
                seeOrSkip: '',
            }
        }
    }

    getMovieInformation(movie) {
        axios.get("https://api.themoviedb.org/3/search/movie", {
            params: {
                api_key : '146fa0756d99220f8811aceb8a865301',
                language : 'en-US',
                query : movie.movieName,
                page : '1',
                include_adult : 'false' 
            }
        })
        .then(apiResponse => {
            const movieDetails = {
                movieId : apiResponse.data.results[0].id,
                movieName : apiResponse.data.results[0].title,
                movieOverview : apiResponse.data.results[0].overview,
                movieReleaseDate : apiResponse.data.results[0].release_date,
                poster_path : apiResponse.data.results[0].poster_path
            }

            return movieDetails
        })
        .catch(error => {
            console.log(error)
        })
    }

    getMovieSentiment(movie) {
        axios.get("http://localhost:5000/movieSentiment/findMovieSentiment/" + movie.movie_id)
        .then(apiResponse => {
            if (apiResponse.data !== null) {
                this.setState({
                    movie_found : true,
                    positive_comments: apiResponse.data.positive_comments,
                    negative_comments: apiResponse.data.negative_comments,
                    neutral_comments: apiResponse.data.neutral_comments,
                    see_or_skip: apiResponse.data.see_or_skip,
                    date_analysed: apiResponse.data.date_analysed
                 })
            } else {
            return
            }
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        firstMovieDetails = this.getMovieInformation(this.state.firstMovieToCompare) 
        secondMovieDetails = this.getMovieInformation(this.state.secondMovieToCompare)
    }

    render() {
        return (
            <div>
                <h1>Movie Results</h1>
            </div>
        )
    }
}

export default CompareMovieResults;
