import React from 'react';
import axios from 'axios'
import {Autosuggest} from 'react-autosuggest'

class CompareMovieSelector extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            movie_entered : "",
            movie_suggestions : [],
            all_movies : []
        }
    }

    getAllMovies() {
        axios.get("http://localhost:5000/movieSentiment/findMovieSentiment")
        .then(apiResponse => {
            this.setState({
                all_movies : apiResponse.data
            })
            console.log(this.state.all_movies)
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        this.getAllMovies()
    }

    getSuggestions = value => {
        const inputValue = value.trim().toLowerCase();
        const inputLength = inputValue.length;
      
        return inputLength === 0 ? [] : this.state.all_movies.filter(movie=>
          movie.movie_title.toLowerCase().slice(0, inputLength) === inputValue
        );
      };
      
    getSuggestionValue = movie_suggestion => movie_suggestion.movie_title;
      
    renderSuggestion = movie_suggestion => (
        <div>
            {movie_suggestion.movie_title}
        </div>
    );

    onChange = (event, { newValue }) => {
        this.setState({
            movie_entered: newValue
        });
    };

    onSuggestionsFetchRequested = ({ value }) => {
        this.setState({
          movie_suggestions: getSuggestions(value)
        });
    };
    
    onSuggestionsClearRequested = () => {
        this.setState({
          movie_suggestions: []
        });
    };
    
    render() {
        const { value, suggestions } = this.state;
    
        const inputProps = {
          placeholder: 'Enter A Movie',
          value,
          onChange: this.onChange
        };
    
        return (
          <Autosuggest
            suggestions={suggestions}
            onSuggestionsFetchRequested={this.onSuggestionsFetchRequested}
            onSuggestionsClearRequested={this.onSuggestionsClearRequested}
            getSuggestionValue={getSuggestionValue}
            renderSuggestion={renderSuggestion}
            inputProps={inputProps}
          />
        );
    }
}

export default CompareMovieSelector;
