import React from 'react';
import {Bar} from 'react-chartjs-2';

class MovieVisualisation extends React.Component {
    constructor(props) {
        super(props)
        this.state = {
            labels : ["Positive", "Negative", "Neutral"],
            datasets : [
                {
                    label : "Twitter Sentiment",
                    data : [Number(this.props.twitter_positive_comments), Number(this.props.twitter_negative_comments), Number(this.props.twitter_neutral_comments)],
                    backgroundColor : ["blue", "blue", "blue"],
                    borderColor : "white"
                },

                {
                    label : "YouTube Sentiment",
                    data : [Number(this.props.youtube_positive_comments), Number(this.props.youtube_negative_comments), Number(this.props.youtube_neutral_comments)],
                    backgroundColor : ["red", "red", "red"],
                    borderColor : "white"
                }
            ]
        }
    }

    componentDidUpdate(prevProps) {
        if (prevProps !== this.props) {
            this.setState({
                datasets : [
                    {
                        label : "Twitter Sentiment",
                        data : [Number(this.props.twitter_positive_comments), Number(this.props.twitter_negative_comments), Number(this.props.twitter_neutral_comments)],
                        backgroundColor : ["blue", "blue", "blue"],
                        borderColor : "white"
                    },
    
                    {
                        label : "YouTube Sentiment",
                        data : [Number(this.props.youtube_positive_comments), Number(this.props.youtube_negative_comments), Number(this.props.youtube_neutral_comments)],
                        backgroundColor : ["red", "red", "red"],
                        borderColor : "white"
                    }
                ]
            })
        }
    }

    render() {
        return (
            <div>
                <Bar
                    data={{
                        labels : this.state.labels,
                        datasets : this.state.datasets
                    }}
                    width={700}
                    height={400}
                    options={{ 
                        maintainAspectRatio: false,
                        scales: {
                            xAxes: [{
                              display: true,
                              gridLines: {
                                display: true,
                                color: "#FFFFFF"
                              },
                              scaleLabel: {
                                display: true,
                                labelString: 'Sentiment'
                              }
                            }],
                            yAxes: [{
                              display: true,
                              gridLines: {
                                display: true,
                                color: "#FFFFFF"
                              },
                              scaleLabel: {
                                display: true,
                                labelString: 'Value'
                              }
                            }]
                        }
                    }}
                />
            </div>
        )   
    }
}

export default MovieVisualisation;
