import React from 'react';
import axios from 'axios'
import Autosuggest from 'react-autosuggest'

class CompareMovieSelector extends React.Component {
    constructor() {
        super()
        this.state = {
            movie_entered : "",
            movie_suggestions : [],
            all_movies : []
        }
    }

    getAllMovies() {
        axios.get("http://localhost:5000/movieSentiment/findMovieSentiment")
        .then(apiResponse => {
            this.setState({
                all_movies : apiResponse.data
            })
            console.log(this.state.all_movies)
        })
        .catch(error => {
            console.log(error)
        })
    }

    componentDidMount() {
        this.getAllMovies()
    }
    
    render() {
        return (
            <div className="input-row">
                <label htmlFor="movie_suggestion">Movie Title</label>
                <Autosuggest 
                    inputProps={{
                        placeholder: "Enter a movie",
                        autoComplete: "abcd",
                        name : "movie_suggestion",
                        id : "movie_suggestion",
                        value : this.state.movie_entered,
                        onChange: (event, {newValue}) => {
                            this.setState({
                                movie_entered: newValue
                            })
                        }
                    }} 
                    suggestions={this.state.movie_suggestions}
                    onSuggestionsFetchRequested={async ({value}) => {
                        if (!value) {
                            this.setState({
                                movie_suggestions : []
                            })
                        }
                    }}
                    onSuggestionsClearRequested={() =>  {
                        this.setState({
                            movie_suggestions : []
                        })
                    }}
                    getSuggestionValue={suggestion => suggestion.movie_title}
                    renderSuggestion={suggestion => <div>{suggestion.movie_title}</div>}
                />
            </div>
        )
    }
}

export default CompareMovieSelector;
